    @Test
    void canInsert() {
        LinkedList Test = new LinkedList();
        Test.insert("20");
        Test.insert("50");
        Test.insert("100");
        String expected = " { 100 }  --->  { 50 }  --->  { 20 }  --->  NULL ";
        assertEquals(expected, Test.toString());
    }

